const tagContainer = document.querySelector('.tag-container')
const tagInput = document.querySelector('#input-skills')
const toolsTagContainer = document.querySelector('.tools-tag-container')
const toolTagInput = document.querySelector('#input-tools')
let tagsOfTools = [];
let tagsOfSkills = [];

// input: string label
// output: div tag element
function createTag(label) {
    const div = document.createElement('div')
    div.setAttribute('class', 'tag')
    const span = document.createElement('span')
    const innerSpan = document.createElement('span')
    innerSpan.innerHTML = label
    const correctIcon = document.createElement('i')
    correctIcon.setAttribute('class', 'ico-true')
    span.appendChild(correctIcon)
    span.appendChild(innerSpan)
    const closeBtn = document.createElement('i')
    closeBtn.setAttribute('class', 'fa fa-times-circle skill-tag')
    closeBtn.setAttribute('data-item', label)
    div.appendChild(span)
    div.appendChild(closeBtn)
    return div;
}

function reset() {
    document.querySelectorAll('.tag').forEach(tag => {
        tag.parentElement.removeChild(tag)
    })
}

function addTags() {
    reset();
    tagsOfSkills.slice().reverse().forEach(tag => {
        const tagElement = createTag(tag)
        tagContainer.prepend(tagElement)
    })
}

try {
    //I put this in try catch block to avoid nullable value of toolTanInput when navigating to another page
    // I propose to removeEventListener after navigating to another page
    tagInput.addEventListener('keyup', function (e) {
        if (e.key === 'Enter') {
            tagsOfSkills.push(tagInput.value)
            addTags()
            tagInput.value = ''
        }
    })
} catch (error) { }


document.addEventListener('click', function (e) {
    if (e.target.tagName === 'I' && e.target.classList.contains('skill-tag')) {
        const value = e.target.getAttribute('data-item')
        const index = tagsOfSkills.indexOf(value)
        tagsOfSkills = [...tagsOfSkills.slice(0, index), ...tagsOfSkills.slice(index + 1)]
        addTags();

    }
})


// input: string label
// output: div tag element
function createToolTag(label) {
    const div = document.createElement('div')
    div.setAttribute('class', 'tool-tag')
    const span = document.createElement('span')
    const innerSpan = document.createElement('span')
    innerSpan.innerHTML = label
    const correctIcon = document.createElement('i')
    correctIcon.setAttribute('class', 'ico-true')
    span.appendChild(correctIcon)
    span.appendChild(innerSpan)
    const closeBtn = document.createElement('i')
    closeBtn.setAttribute('class', 'fa fa-times-circle tool-close-tag')
    closeBtn.setAttribute('data-item', label)
    div.appendChild(span)
    div.appendChild(closeBtn)
    return div;
}

function resetToolTags() {
    document.querySelectorAll('.tool-tag').forEach(tag => {
        tag.parentElement.removeChild(tag)
    })
}

function addToolTags() {
    resetToolTags();
    tagsOfTools.slice().reverse().forEach(tag => {
        const tagElement = createToolTag(tag)
        toolsTagContainer.prepend(tagElement)
    })
}

try {
    //I put this in try catch block to avoid nullable value of toolTanInput when navigating to another page
    // I propose to removeEventListener after navigating to another page
    toolTagInput.addEventListener('keyup', function (e) {
        if (e.key === 'Enter') {
            tagsOfTools.push(toolTagInput.value)
            addToolTags()
            toolTagInput.value = ''
        }
    })
} catch (error) { }

document.addEventListener('click', function (e) {
    if (e.target.tagName === 'I' && e.target.classList.contains('tool-close-tag')) {
        const value = e.target.getAttribute('data-item')
        const index = tagsOfTools.indexOf(value)
        tagsOfTools = [...tagsOfTools.slice(0, index), ...tagsOfTools.slice(index + 1)]
        addToolTags();
    }
})

// Switch Circle

try {
    document.addEventListener('DOMContentLoaded', function () {
        var checkbox = document.querySelector('#customSwitch3');
        if (checkbox) {
            checkbox.addEventListener('change', function () {
                let myCirclesForm = document.querySelectorAll('.hide-me')
                let paidToYouForm = document.querySelector('.show-me')
                if (checkbox.checked) {
                    myCirclesForm.forEach(nd => {
                        nd.classList.add('d-block')
                        nd.classList.remove('d-none')
                    })

                    paidToYouForm.classList.add('d-none')
                    paidToYouForm.classList.remove('d-block')

                } else {
                    myCirclesForm.forEach(nd => {
                        nd.classList.remove('d-block')
                        nd.classList.add('d-none')
                    })

                    paidToYouForm.classList.add('d-block')
                    paidToYouForm.classList.remove('d-none')
                }
            });
        }
    });

} catch (error) { }


