    /*******************************************
    	-	PREPARE LOADING OF IMAGES	-
    ********************************************/
    function loadAllPrepared(container, alreadyinload) {

        container.find('img, .defaultimg').each(function(i) {
            var img = jQuery(this);

            if (img.data('lazyload') != img.attr('src') && alreadyinload < 3 && img.data('lazyload') != undefined && img.data('lazyload') != 'undefined') {

                if (img.data('lazyload') != undefined && img.data('lazyload') != 'undefined') {
                    img.attr('src', img.data('lazyload'));

                    var limg = new Image();

                    limg.onload = function(i) {
                        img.data('lazydone', 1);
                        if (img.hasClass("defaultimg")) setDefImg(img, limg);
                    }
                    limg.error = function() {
                        img.data('lazydone', 1);
                    }

                    limg.src = img.attr('src');
                    if (limg.complete) {
                        if (img.hasClass("defaultimg")) setDefImg(img, limg);
                        img.data('lazydone', 1);
                    }

                }
            } else {

                if ((img.data('lazyload') === undefined || img.data('lazyload') === 'undefined') && img.data('lazydone') != 1) {
                    var limg = new Image();
                    limg.onload = function() {
                        if (img.hasClass("defaultimg")) setDefImg(img, limg);
                        img.data('lazydone', 1);
                    }
                    limg.error = function() {
                        img.data('lazydone', 1);
                    }


                    if (img.attr('src') != undefined && img.attr('src') != 'undefined') {
                        limg.src = img.attr('src');
                    } else
                        limg.src = img.data('src');

                    if (limg.complete) {
                        if (img.hasClass("defaultimg")) {
                            setDefImg(img, limg);
                        }
                        img.data('lazydone', 1);
                    }
                }
            }
        })
    }

    function setDefImg(img, limg) {
        var nextli = img.closest('li');
        var ww = limg.width;
        var hh = limg.height;
        nextli.data('owidth', ww);
        nextli.data('oheight', hh);
        nextli.find('.slotholder').data('owidth', ww);
        nextli.find('.slotholder').data('oheight', hh);
        nextli.data('loadeddone', 1);
    }

    var waitForLoads = function(element, call, opt) {

        loadAllPrepared(element, 0)
        var inter = setInterval(function() {
            opt.bannertimeronpause = true;
            opt.container.trigger('stoptimer');
            opt.cd = 0;
            var found = 0;
            element.find('img, .defaultimg').each(function(i) {
                if (jQuery(this).data('lazydone') != 1) {
                    found++;

                }
            });

            if (found > 0)
                loadAllPrepared(element, found);
            else {
                clearInterval(inter);
                if (call != undefined) call();
            }

        }, 100)
    }


    //////////////////////////////
    //                         //
    //	-	SWAP THE SLIDES -  //
    //                        //
    ////////////////////////////
    function swapSlide(container, opt) {
        try {
            var actli = container.find('>ul:first-child >li:eq(' + opt.act + ')');
        } catch (e) {
            var actli = container.find('>ul:first-child >li:eq(1)');
        }
        opt.lastslide = opt.act;
        var nextli = container.find('>ul:first-child >li:eq(' + opt.next + ')');

        var defimg = nextli.find('.defaultimg');


        opt.bannertimeronpause = true;
        container.trigger('stoptimer');
        opt.cd = 0;

        if (defimg.data('lazyload') != undefined && defimg.data('lazyload') != "undefined" && defimg.data('lazydone') != 1) {

            if (!isIE(8))
                defimg.css({ backgroundImage: 'url("' + nextli.find('.defaultimg').data('lazyload') + '")' });
            else {
                defimg.attr('src', nextli.find('.defaultimg').data('lazyload'));
            }

            defimg.data('src', nextli.find('.defaultimg').data('lazyload'));
            defimg.data('lazydone', 1);
            defimg.data('orgw', 0);
            nextli.data('loadeddone', 1);

            TweenLite.set(container.find('.tp-loader'), { display: "block", opacity: 0 });
            TweenLite.to(container.find('.tp-loader'), 0.3, { autoAlpha: 1 });

            waitForLoads(nextli, function() {
                swapSlideCall(opt, defimg, container)
            }, opt);

        } else {

            if (nextli.data('loadeddone') === undefined) {
                nextli.data('loadeddone', 1);
                waitForLoads(nextli, function() {
                    swapSlideCall(opt, defimg, container)
                }, opt);
            } else

                swapSlideCall(opt, defimg, container)
        }
    }

    function swapSlideCall(opt, defimg, container) {
        opt.bannertimeronpause = false;

        opt.cd = 0;
        container.trigger('nulltimer');
        TweenLite.to(container.find('.tp-loader'), 0.3, { autoAlpha: 0 });
        setSize(defimg, opt);
        setBulPos(container, opt);
        setSize(defimg, opt);
        swapSlideProgress(container, opt);

    }

